$("#emailChangeButton").click(function(){var e=$("#emailChangeHash").val(),a=$("#token").val();$.ajax({url:"resources/handler/mailChange.php",data:{event:"change",hash:e,token:a},type:"POST"}).done(function(e){1==e?Materialize.toast("Email was succesfully changed!",4e3,"rounded",function(){location.href="home.php"}):0==e?Materialize.toast("Email Link was not recognized, no changes was made",4e3,"rounded",function(){location.href="home.php"}):Materialize.toast("Sorry, An unexpected error accured",4e3,"rounded")}).fail(function(){})}),$("#emailChangeCancel").click(function(){var e=$("#emailChangeHash").val(),a=$("#token").val();$.ajax({url:"resources/handler/mailChange.php",data:{event:"cancel",hash:e,token:a},type:"POST"}).done(function(e){1==e?Materialize.toast("Email change was canceled!",4e3,"rounded",function(){location.href="home.php"}):0==e?Materialize.toast("Email Link was not recognized, no changes was made",4e3):Materialize.toast("Sorry, An unexpected error accured",4e3,"rounded")}).fail(function(){})});